mapper.select:
 		select * from
		(select rownum as rnum,a.* from
		(select * from hallreview order by review_idx desc)a)b
		where rnum>=#{startnum}
		and rnum<=#{endnum}

 	select * from
		(select rownum as rnum,a.* from
		(select * from hallreview order by review_idx desc)a)b
		where rnum>=#{startnum}
		and rnum<=#{endnum} and writer=#{name}

 select * from
(select rownum as rnum,a.* from
(select * from hallreview where name=#{hallName} order by review_idx desc)a)b
where rnum>=#{startnum}
and rnum<=#{endnum}
